$('form').on('keypress',function(event){let press=event.keyCode||event.which
if(press===13){event.preventDefault()
return!1}})
function getToken(){let content=$('meta[name="csrf_token"]').attr('content')
if(content!==undefined&&content!==''){return'csrf_token='+encodeURIComponent(content)+'&'}
return''}
function addToken(content){$('meta[name="csrf_token"]').attr('content',content)}
function submitButton(action,div,button){if(action==='start'){if($(button).attr('loading')===!0){return!1}
$(div).removeClass('disabled').addClass('inactive')
$(button).attr('loading',!0).removeClass('disabled').addClass('inactive')
$(button).attr('data-text',$(button).html())
$(button).css('width',$(button).outerWidth()).html('<i class="fa-solid fa-spinner fa-spin"></i>')}
if(action==='close'){$(div).addClass('disabled').removeClass('inactive')
$(button).attr('loading',!1).addClass('disabled').removeClass('inactive')
$(button).attr('disabled',!0)
$(button).css('width','').html($(button).attr('data-text'))}
if(action==='active'){$(parent).removeClass('disabled').removeClass('inactive')
$(button).attr('loading',!1).removeClass('disabled').removeClass('inactive')
$(button).attr('disabled',!1)
$(button).css('width','').html($(button).attr('data-text'))}}